import React, {createContext} from 'react';
import auth from "../firebase.js";

export const UserContext = createContext({user:null});

class UserProvider extends React.Component {
  state = {
    user: {
      email: "noah13wotring@outlook.com",
      password: "123",
      displayName: "hello all",
      photoURL:'https://lh3.googleusercontent.com/proxy/vzOX1KBH9QqNijnYZbx9I7-D_VbtaKkL3oYUupVZbt0jOFYsOY1YYpI9OqoN9JyrxFghCsfuHmrmmj6n-Ai3BokbfzgI8wBwtt3oSZeQqE6_ohlVqmyitL1LYxueO2_ve-sPvqjp6FmpfJw'
    }
  };
  componentDidMount = () => {
    auth.onAuthStateChanged(userAuth => {
      this.setState({
        user: userAuth
      });
    });
  };
  render() {
    return(
      <UserContext.Provider value={this.state.user}>
        {this.props.children}
      </UserContext.Provider>
    );
  }
}
export default UserProvider;
