      let d = new Date();
      let h = d.getHours();
      let m = d.getMinutes();
      let s= d.getSeconds();
      console.log(`${h}:${m}:${s}`);
      setTime({
        h: h,
        m: m,
        s: s
      })


    },1000)


  }, [])
  const [time,setTime ] = useState(null)

  useEffect(() => {
    rotateHandler()
  },[time])

  const rotateHandler = () => {
    document.querySelector('.a').style.transform(`rotate(${6*time.s}deg)`)
  }


return (

  <div className="sundial_body" >
    <div className="sundial_pane a " />
    <div className="sundial_pane b" />
    <div className="sundial_pane c" />
    <div className="sundial_pane d"  />

  </div>



)



}
export default SunDial;
