import React, {Component, createContext, useContext} from 'react';

export const TheNumContext = createContext(13);

class GiveNumProvider extends Component {

  constructor(){
    super();
    state = {
      theNum: useContext(TheNumContext),
    }
  }
  render(){
    return(
      <TheNumContext.Provider value={this.state.theNum}>
        <this.props.children />
      </TheNumContext.Provider>
    )
  }

}

export default GiveNumProvider;
