import React, { useState, useEffect } from 'react';
import './css/sundial.css';

const SunDial = () => {

  useEffect(() => {

    let d = new Date();
    let h = d.getHours();
    let m = d.getMinutes();
    let s= d.getSeconds();
    console.log(`${h}:${m}:${s}`);
    let ind = setInterval(() => {
      setTime(returnTimeObjectHandler())
    },1000)



  }, [time])

  const [time,setTime ] = useState({
    h: new Date().getHours(),
    m: new Date().getMinutes(),
    s: new Date().getSeconds()

  })

  const returnTimeObjectHandler = () => {
    let d = new Date();
    return {
      h: d.getHours(),
      m: d.getMinutes(),
      s: d.getSeconds()
    }
  }
  const rotateHandler = () => {
    document.querySelector('.a').style.transform(`rotate(${6*time.m}deg)`)
  }


return (

  <div className="sundial_body" >
    <div className="sundial_pane a " />
    <div className="sundial_pane b" />
    <div className="sundial_pane c" />
    <div className="sundial_pane d"  />

  </div>



)



}
export default SunDial;
