import React, {useContext, Component, useState} from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter, Route, Switch, Link,Redirect, useLocation} from 'react-router-dom';
import SignIn from "./components/SignIn";
import ProfilePage from "./pages/ProfilePage";
import PasswordReset from "./components/PasswordReset";
import Home from "./pages/Home";
import About from "./pages/About";
import SignUp from "./components/SignUp";
import UserProvider,{UserContext} from './providers/UserProvider.jsx';
import logo from './img/logo.png';
import {auth} from './firebase.js';
import './utils/navbar/navbar.css';
/*
import ProfilePage from ".components/ProfilePage";
import PasswordReset from "./components/PasswordReset";
*/
function Application(){

  let user = useContext(UserContext);
  let location = useLocation();
  function inUpOut(){
    console.log(location);
    if(user){
      return <button onClick={() => auth.signOut() }> Sign Out </button>
    } else if (location.pathname === '/SignIn'){
      return  <Link to="./SignUp"> Sign Up </Link>
    } else {
      return  <Link to="./SignIn"> Sign In </Link>
    }
  }


    return(
      <BrowserRouter>
      <div className="navbar mx-auto bg-gray-600 w-full shadow-md block">
        <div className="signinBox mr-5 float-left">
           {!user?
          <Link to="/SignIn">
          <img src={logo} className="userPhoto hover:text-white-300" />
          </Link>
          :
          <Link to="/ProfilePage">
          <img src={user.photoURL} className="userPhoto " />
          </Link>
          }
        </div>
        <ul className="my-5 ">
          <li className="mx-5 w-15 float-left hover:bg-gray-500 hover:text-blue-100">
            <Link to="/Home">Home</Link>
          </li>
          <li className="mx-5 w-15 float-left hover:bg-gray-500 hover:text-blue-100">
            <Link to="/About">About</Link>
          </li>
          <li className="mx-5 w-15 hover:bg-gray-500 float-left hover:text-blue-100">
            <Link to="/News">News</Link>
          </li>
          <li className="mx-5 w-15 hover:bg-gray-500  float-left hover:text-blue-100">
            <Link to="/Zombie Math">Zombie Math</Link>
          </li>
          </ul>
          <div className="inOut float-right mx-5  hover:bg-gray-500 hover:text-blue-100" >
          {inUpOut()}
          </div>

      </div>
      <div className="sidebar inline-block ">
      </div>

      <Switch>

        <Route path="/ProfilePage">
          {user?
          <ProfilePage sidebar={false} />
          :
          <Redirect to="/SignIn" />
          }
        </Route>
        <Route path="/Home" exact>
          <Home />
        </Route>
        <Route path="/About">
          <About />
        </Route>
        <Route path="/PasswordReset" >
          <PasswordReset />
        </Route>
        <Route path="/SignIn" >
          {user?
          <Redirect to="/ProfilePage" />
          :
          <SignIn />
          }
        </Route>
        <Route path="/SignUp" exact>
        <SignUp />
        <Route path="/Home" exact>
          <Home />
        </Route>

        </Route>




      </Switch>


      </BrowserRouter>


    )
}


export default Application;
