import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import myFirebase from './firebase.js';
import Signup from './utils/SignupForm';
import './App.css';


class App extends Component {

  constructor(){
    super();
    this.state = {
      allUsers: [ ],
    }
    this.firebaseDB = myFirebase.getFirebaseRef('Users');
    this.firebaseDB.once('value').then((snapshot) => {
      console.log(snapshot.val());
      this.addUser(snapshot.val());
    } )

  }

  componentDidMount(){
    this.firebaseDB = myFirebase.getFirebaseRef('Users');
    this.firebaseDB.once('value').then((snapshot) => {
      console.log(snapshot.val());
      this.addUser(snapshot.val());
    } )
  }
  addUser(users){
    let idNum = this.state.allUsers.length;

    let newUsers = Object.assign([], this.state.allUsers);
    console.log(idNum);
      for(let key in users){
        newUsers.push({
          name: users[key].name,
          username: users[key].username,
          password: users[key].age
        });

    }
    this.setState({
      allUsers: newUsers,
    })
  }
  handleDBChange(event){

  }
  changeIt = () =>
     <ul id="userTree">
      {this.state.allUsers.map((user) => <li>{user.username} {user.name} </li> )}

    </ul>

  render(){

    return(
      <div>
      <h1> Hello! </h1>
      <Signup myFirebaseDB={this.firebaseDB} userBase={this.state.allUsers} />;
      <h2>{this.state.allUsers.length} </h2>
      {this.changeIt()}
      </div>
    )
  }








}

export default App;
