import React, {Component, createContext} from 'react';
import {auth, generateUserDocument} from 'C:\\Users\\Noah Wotring\\Desktop\\Code\\ReactUserDataPractice\\my-app\\src\\firebase.js';

export const UserContext = createContext({user: null});

class UserProvider extends Component {
  state = {
    user: {
      photoURL: 'https://lh3.googleusercontent.com/proxy/vzOX1KBH9QqNijnYZbx9I7-D_VbtaKkL3oYUupVZbt0jOFYsOY1YYpI9OqoN9JyrxFghCsfuHmrmmj6n-Ai3BokbfzgI8wBwtt3oSZeQqE6_ohlVqmyitL1LYxueO2_ve-sPvqjp6FmpfJw',
      email: "noah13wotring@outlook.com",
      password: "L0w_L3v31",
      displayName: "DatHomieSilverSurfer"
    },
  };
  componentDidMount = async () => {
    auth.onAuthStateChanged(async userAuth => {
      const user = await generateUserDocument(userAuth);
      this.setState({user:userAuth});
    });

  }


  render() {
    return (
      <UserContext.Provider value={this.state.user}>
        {this.props.children}
      </UserContext.Provider>
    );
  }
}
export default UserProvider;
