import React, {Component, useContext,useState, useEffect, setTimeOut} from 'react';
import SignIn from "../components/SignIn";
import NewsPost from '../components/NewsPost.js';
import './home.css';
import firebase,{generateNewsPosts,firestore}from  '../firebase.js'
import {UserContext} from "../providers/UserProvider";
import {TheNumContext} from '../providers/NumberProvider';

const Home = () => {
  const user = useContext(UserContext);
  const num = useContext(TheNumContext);
  const[posts,checkPosts] = useState([]);
  const [potentialPost,editPotentialPost] = useState(" ");
  const [potentialPostTitle,editPotentialPostTitle] = useState(" ");


  const onChangeHandler = (event) => {
    if(event.target.id === "potentialPostText"){
      editPotentialPost(event.target.value);
      console.log(potentialPost);
    }
    if(event.target.id === "potentialPostTitle"){
      editPotentialPostTitle(event.target.value)
    }
  }

 //
  const generateNewsPosts = async () => {
    let collectedPosts = [];
    const postsRef = firestore.collection('posts');
    const snapshot = await postsRef.get();
    try {
      const allPosts = await firestore.collection(`posts`).get();
      allPosts.forEach(doc => {
        collectedPosts.push({title: doc.data().title,
                              body: doc.data().body,
                              owner_id: doc.data().owner_id,
                              owner : doc.data().owner,
                              id: doc.id
                            })
      });
      collectedPosts.forEach((doc) => console.log(doc));
      checkPosts(collectedPosts.sort(function(a,b){return b.id-a.id}));
      } catch (error) {
        console.error("Error fetching data", error)
      }
    }
    //submitting new post
    const submitNewPost = async (postTitle,postBody) => {
      var endId = posts.length + 1;
      var owner_id = user? user.uid : null;
      var owner = user? user.displayName : null;
      var postData = {
        owner_id: owner_id,
        title: postTitle,
        body: postBody,
        owner: user.name,


      };
      console.log(postData);

      try {
      const res = await firestore.collection('/posts/').doc(`${endId}`).set({
        owner_id: owner_id,
        owner: owner,
        title: postTitle,
        body: postBody,
        date_created: new Date()


      });
      return
    } catch {
      alert('failure with' + postData.owner + postData.owner_id + postData.title + postData.body)
    }
    }




  useEffect(() => {
    generateNewsPosts();
  },[])

    return (


      <div className="inline-block w-9/12 mx-auto" >
        <h1 className="text-center text-5xl mt-3 font-sans font-semibold"> Home Yay</h1>
        <button onClick={(event) => console.log()}> click </button>
        <div className="container news_post_container ml-8   opacity-75 box item-center ">
          {posts.sort((a,b) => {return a.date_created-b.date_created}).map((post) => <NewsPost body={post.body} title={post.title} owner_id={post.owner_id} owner={post.owner} />)}
          <div className="submitPost container block bg-grey-600 rounded-lg">
            <div className="form block flex box-content w-full">
            <div className="center_block m-auto w-1/4 ">
              <h3 className="text-center my-5"> Submit a New Post </h3>

              <form>
                <div className="labelinputblock label_block inline-block">
                  <label className=""> Title {" "} </label>
                  <input
                    className="title_draft"
                    onChange={(event) => onChangeHandler(event)}
                    id="potentialPostTitle"
                  />
                </div>
                <br /> <br />{" "}
                <textarea
                  className="post_draft block"
                  value={potentialPost}
                  id="potentialPostText"
                  onChange={((event) => onChangeHandler(event))}
                  />
                  <button className="block float-right m-3 shadow  " onClick={() => submitNewPost(potentialPostTitle,potentialPost)}> Submit </button>
                </form>
                </div>
              </div>
           </div>

         </div>


      </div>
    )
  }


export default Home;
