      <div>
        <h3 className=""> Home </h3>
        <div className="container news_post_container w-4/5 flex flex-box box item-center ">
        {posts.forEach(post => (<NewsPost title={post.title} body={post.body} />))}
         </div>

      </div>
    )
  }


export default Home;
