import {generateNewsPosts,firestore}from  '../firebase.js'
import {UserContext} from "../providers/UserProvider";
import {TheNumContext} from '../providers/NumberProvider';

const Home = () => {
  const user = useContext(UserContext);
  const num = useContext(TheNumContext);
  const mount = useEffect(() => {

  });

  const generateNewsPosts = async () => {
    let collectedPosts = [];
    const postsRef = firestore.collection('posts');
    const snapshot = await postsRef.get();
    try {
      const allPosts = await firestore.collection(`posts`).get();
      allPosts.forEach(doc => {
        collectedPosts.push({title: doc.data().title,
                              body: doc.data().body
                            })
      });
      return collectedPosts
      } catch (error) {
        console.error("Error fetching data", error)
      }
    }



  const[posts,checkPosts] = useState([generateNewsPosts()]);

  useEffect(() => {
    generateNewsPosts();
  })

    return (


      <div>
        <h3 className=""> Home Yay</h3>
        <button onClick={generateNewsPosts()}> click </button>
        <div className="container news_post_container w-4/5 flex flex-box box item-center ">
         </div>

      </div>
    )
  }


export default Home;
