import React, {useContext, createState} from 'react';
import {UserProvider} from '../providers/UserProvider';
import './newspost.css';
const NewsPost = (props) => {
  const title = props.title;
  const body = props.body;





  const [expanded, expand] = createState(false);
  

  return (
    <div className={`newpost mt bg-red-500 mx-4 my-2 py-2 border-opacity-75 hover:bg-red-600 hover:opacity-75  ${expanded? "max-h-full" : "overflow-hidden"} `}  onClick={(event) => expand(!expanded)}>
      <h4> {title} </h4>
      <p> {body} </p>
    </div>


  )



}
export default NewsPost;
