import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import myFirebase from './firebase.js';
import Signup from './utils/SignupForm';
import './App.css';


class App extends Component {

  constructor(){
    super();
    this.state = {
      allUsers: [ ],
    }
    this.firebaseDB = myFirebase.getFirebaseRef();
    this.firebaseDB.once('value').then((snapshot) => {
      console.log(snapshot.val().username + " is the name of the first snapshot");
      this.addUser(snapshot.val());
    } )

  }

  componentDidMount(){

  }
  addUser(users){
    let newUsers = Object.assign([], this.state.allUsers);
    if(users){
      for(let key in users){
        newUsers.push({
          name: users[key].name,
          username: users[key].username,
          password: users[key].password
        });
      }
    }
    this.setState({
      allUsers: newUsers,
    })
  }
  render(){
    return(
      <div>
      <h1> Hello! </h1>
      <ul id="userTree">


      </ul>
      </div>
    )
  }








}

export default App;
