import React, {useState,useEffect} from 'react';
import './newspost.css';
const NewsPost = ({title,body,owner_id,owner,date_created,id,postNum}) => {




  useEffect(() => {
  },[])

  const [expanded, expand] = useState(false);


  return (
    <div style={{
      width: '100%',
      textAlign: 'center',
      background: `${postNum%2===0 ? "linear-gradient(to top,  #ffffff 0%,#EFEFEF 100%)" : "linear-gradient(to bottom,  #ffffff 0%,#EFEFEF 100%)"}`,
      boxShadow: 'inset 0 0 0 5px rgba(255,255,255,0.1)',
      transform: `${postNum%2===0 ? "transform: perspective(100px) rotateX(-3deg) translateY(3px)" : " transform: perspective(100px) rotateX(3deg) translateY(2px) scale(1.001)"}`

    }}>


    <div className={`newpost  mx-4 my-2 mb-4 hover:border text-center mx-auto text-black  relative rounded-md  w-9/12   ${expanded? "max-h-full " : "overflow-hidden"} `}  onClick={(event) => expand(!expanded)}>

      <div className="inline-block left-0  absolute">
        <div className="uid inline "><span className="float-left mt-2 text-xs"> Post By: {owner}</span> </div>
      </div>


      <div className="inline-block ">
        <h2 className="text-center mt-4 mb-2"> {title}</h2>
        <p className="text-center mx-8 mb-2  "> {body} </p>
      </div>

      <div className="inline-block  absolute float-right top-0 right-0 mr-0">
        <span className=" text-xs"> {date_created} </span>
      </div>
      <p className={`${expanded? "invisible" : "visible" }`}> ...</p>

    </div>
    </div>



  )



}
export default NewsPost;
