import React, {useState,userContext,useEffect} from 'react';
import './zombie_math.css';
import {UserContext} from "../providers/UserProvider";

const ZombieMath = (props) => {
  const user = props.user;
  const [score,changeScore] = useState(0);
  const [activated,activatedGame] = useState(false);
  const [currentAnswer,changeAnswer] = useState(0);
  const [lifepoints,changeLifePoints] = useState(5);
  const [numberSet, genNumbers] = useState({
    num1: null,
    num2: null,
    answer: null
  });
  const generateNumbers = () => {
        let num1 = Math.floor(Math.random() * 100);
        let num2 = Math.floor(Math.random() * 100);
        let answer = num1 + num2;
        genNumbers({
          num1: num1,
          num2: num2,
          answer: answer
        })
      }
  useEffect(() => {
    generateNumbers();

  },[])








  return (
      <div className="zombie_math_background">


       <h1> Hello </h1>
       <div className="bg-grey-300 w-1/2 h-32 block">
       <span className="num1">{numberSet.num1} </span><span className="num2">{numberSet.num2} </span>
       <button onClick={() => generateNumbers() }>Click Here </button>
       </div>



        </div>
  )










}

export default ZombieMath;
