import React, {useState} from 'react';
import './colorplay.css'
const ColorPlay = () => {
  const inputs = document.querySelectorAll('.controls input');
  const handleUpdate = (e) => {
    document.documentElement.style.setProperty(`--${e.currentTarget.id}`,e.currentTarget.value);
  }
  inputs.forEach.addEventLisener('change',handleUpdate)
return (
  <div className="block">
  <div className="color-container inline-block">
  </div>
  <div className="inline-block">
    <label for="red"> R </label>
    <input type="range" id="colorplay-red" min="0" max="255"  />
    <label for="green"> G </label>
    <input type="range" id="colorplay-green" min="0" max="255"  />
    <label for="blue"> B </label>
    <input type="range" id="colorplay-blue" min="0" max="255"  />

  </div>
  </div>
)

}

export default ColorPlay;
