import React, {useState,userContext,useEffect} from 'react';
import './zombie_math.css';
import {UserContext} from "../providers/UserProvider";

const ZombieMath = (props) => {
  const user = props.user;
  const [gameIsRunning, setRunGame] = useState(false);
  const [timeLeft,setTime] = useState(0);
  const [timerIsRunning,setTimer] = useState(false);
  const [score,changeScore] = useState(0);
  const [currentAnswer,changeAnswer] = useState("");
  const [hp,setHealth] = useState(5);
  const [numberSet, genNumbers] = useState({
    num1: null,
    num2: null,
    answer: null
  });

  //

  //effect that checks game state, starts timer toggle on
  useEffect(() => {
    if(gameIsRunning){
      setTimer(!timerIsRunning);
    }
  },[gameIsRunning])


  //countdown timer. after render/click/toggle game/toggle timer, this runs
  useEffect(() => {
    let interval = null;
    if(timerIsRunning){
      interval = setInterval(() => {
        setTime(timeLeft - 1)
      }, 1000)
    }
    if (timerIsRunning && timeLeft === 0 && hp > 0){
      setHealth(hp-1);
      generateNumbers();
      setTime(10);
    }
    if(hp === 0){
      clearInterval(interval);
      reset();
    }

    return () => clearInterval(interval);
  },[timerIsRunning,timeLeft]);

  const generateNumbers = () => {
        let num1 = Math.floor(Math.random() * 100);
        let num2 = Math.floor(Math.random() * 100);
        let answer = num1 + num2;
        genNumbers({
          num1: num1,
          num2: num2,
          answer: answer
        })
      }
      let intervalHandle;


  useEffect(() => {
    if(gameIsRunning){
      generateNumbers()
    }
  },[gameIsRunning])




  const reset = () => {
    setTime(0);
    setRunGame(!gameIsRunning)
  }

    const toggleGame = () => {
      if(gameIsRunning){
        generateNumbers();
        setHealth(5);
        setTime(10);
        setTimer(!timerIsRunning);
      } else {
        reset();
      }


    }




  const onChangeHandler = (event) => {
    const {name,value} = event.currentTarget;
    if(name === "currentAnswer"){
      changeAnswer(value)
    }
  }

  const handleAnswerSubmit = () => {
    if(currentAnswer === numberSet.answer.toString()){
      changeScore(score + 1);
      setTime(10);
      generateNumbers();
      changeAnswer("");
    } else {
      alert("your answer was " + document.querySelector('#answerAttempt').value  + ", which is FALSE, the current answer is " + numberSet.answer)
      setHealth(hp-1);
      generateNumbers();
      changeAnswer("");
    }
  }

  const handleKeyDown = (e) => {
    console.log(e.key)
    if (e.key == "Enter"){
      handleAnswerSubmit();
    }
  }

  //the containers
  const numberContainer =
    <div className="number container mx-0">
      <span className="num1 inline-block">{numberSet.num1} </span> + <span className="num2">{numberSet.num2} =
      <input
        autoFocus
        onKeyDown={e=>{handleKeyDown(e)}}
        type="integer"
        className="w-1/12 "
        name="currentAnswer"
        value={currentAnswer}
        placeholder=""
        id="answerAttempt"
        onChange = {(event)=> onChangeHandler(event)}
        /> </span>
      </div>

      const startButtonContainer =
      <button className=" absolute bottom-0 right-0 border border-blue-500" onClick={toggleGame}>Start Game </button>










  return (
      <div className="zombie_math_background text-center block">
        <h1> {timeLeft} </h1>

       <h1> Hello, your score is <span className="bg-red-200 border-blue-300 border">{score} </span> </h1>
       <div className="bg-gray-400 w-1/2 h-32 container block my-auto mx-auto border border-red-200 relative">
        {gameIsRunning? numberContainer : startButtonContainer}
        <button className="  border border-blue-500" onClick={() => handleAnswerSubmit() }>Click Here </button>

       </div>



        </div>
  )










}

export default ZombieMath;
