  import React, {Component} from 'react';
  import ReactDOM from 'react-dom';
  import "../App.css";
  import './signupform.css';
  import myFirebase from "../firebase.js";

  class Signup extends Component  {
    constructor(props){
      super(props);
      this.state = {


      }


          this.handleSubmit = this.handleSubmit.bind(this);
          this.handleNameChange = this.handleNameChange.bind(this);
          this.handleUsernameChange = this.handleUsernameChange.bind(this);
          this.handleAgeChange = this.handleAgeChange.bind(this);
          this.handlePasswordChange = this.handlePasswordChange.bind(this);
          this.myFirebaseDB = props.myFirebaseDB;

    }

    componentDidMount(){
    }

    handleNameChange(event){
      this.setState({
        name: event.target.value
      });
    }
    handleUsernameChange(event){
      this.setState({
        username: event.target.value
      });
    }

    handlePasswordChange(event){
      this.setState({
        password: event.target.value

      })
    }
    handleAgeChange(event){
      this.setState({
        age: event.target.value
      })
    }
    handleSubmit(event){
      console.log(`${this.state.username} ${this.state.password} ${this.state.name}`);
      var id = this.props.myFirebaseDB.length;
      console.log("this is" + id);
      this.props.myFirebaseDB.add({
         name: this.state.name,
          username: this.state.username,
          password: this.state.password }

      );
    }


    render(){
      return(
        <div>
          <h1>Welcome, {this.state.name} aka {this.state.username}, {this.state.age} years old </h1>
        <form onSubmit={this.handleSubmit.bind(this)} >
          <label for="realname" id="realname" > First Name:
            <input name="realname" type="text" onChange={this.handleNameChange} value={this.state.name} />
          </label>
          < br/>
          < br/>
          <label for="username" id="username" > Desired Username:
            <input type="text" value={this.state.username} onChange={this.handleUsernameChange} />
          </label>
          < br/>
          < br/>
          <label for="age" id="age"> Age:
            <input type="integer" value={this.state.age}onChange={this.handleAgeChange} />
          </label>
          < br/>
          < br/>
          <label for="password" id="password"> Desired Password:
            <input type="text" value={this.state.password} onChange={this.handlePasswordChange} />
          </label>
          <br />
          <input type="submit" value="Submit" style={{"float":"right", "margin-right":"50%"}} />
        </form>


        </div>)

    }
  }

  export default Signup;
