import React, {useContext, useState} from 'react';
import {UserProvider} from '../providers/UserProvider';
import './newspost.css';
const NewsPost = ({title,body,owner_id,owner,date}) => {






  const [expanded, expand] = useState(false);


  return (
    <div className={`newpost  mx-4 my-2 py-2 text-center  hover:bg-white-600 hover:opacity-75     ${expanded? "max-h-full" : "overflow-hidden"} `}  onClick={(event) => expand(!expanded)}>
    <div className="uid inline"><span className="float-left">{owner}:{owner_id}</span> <span className="float-right"> {date}</span> </div>

      <h3 className="text-center "> {title} </h3>
      <p className="text-center "> {body} </p>
    </div>


  )



}
export default NewsPost;
