import React,{useState, useEffect, useContext} from 'react';
import './repchoose.css';
import rep_grimace from "../img/rep_grimace.jpg";
import rep_slug from "../img/rep_slug.png";
import rep_tobs from "../img/rep_tobs.png";
import rep_joe from "../img/rep_joe.png";
const RepChoose = (user) => {
  const [rep,repChange] = useState(null);

  const handleRepChange = (e) => {

  }
  const handleSelect = (e) => {
    let nextRep = e.target.id;
    let currentRep = rep !== null ? rep : nextRep;

    document.querySelector(currentRep).classList.remove('shadow-lg');
    repChange(e.target.id);
    e.target.classList.add('shadow-lg')
  }


  return(

      <div className="mt-8 my-auto border text-center absolute container mx-32 z-50 block bg-white border shadow-lg  opacity-150 border-black  w-10/12 ">
      <h1> Pick Your Side </h1>
      <div className="page">
      <div className="grid_container flexbox justify-evenly">
        <div id="rep_grimace" className="grid_image_container  inline-block bg-white hover:shadow-lg px-8" onClick={(event) => handleSelect(event)}><div className="image_holder" style={{background:`url('${rep_grimace}') no-repeat center center`, backgroundSize: "cover", height: "200px", width: "200px", overflow: "hidden"}}></div></div>
        <div id="rep_slug" className="grid_image_container  inline-block bg-white hover:shadow-lg px-8" onClick={(event) => handleSelect(event)}><div className="image_holder" style={{background:`url('${rep_slug}') no-repeat center center`, backgroundSize: "cover", height: "200px", width: "200px", overflow: "hidden"}}></div></div>
        <div id="rep_tobs" className="grid_image_container inline-block bg-white hover:shadow-lg px-8" onClick={(event) => handleSelect(event)}><div className="image_holder" style={{background:`url('${rep_tobs}') no-repeat center center`, backgroundSize: "cover", height: "200px", width: "200px",overflow: "hidden"}}></div></div>
        <div id="rep_joe" className="grid_image_container inline-block  bg-white hover:shadow-lg px-8" onClick={(event) => handleSelect(event)}><div className="image_holder" style={{background:`url('${rep_joe}') no-repeat center center`, backgroundSize: "contain", height: "200px", width: "200px",overflow: "hidden"}}></div></div>
        </div>
      </div>


      </div>



  )

}
export default RepChoose;
