import React, {useContext, Component, useState, useEffect} from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter, Route, Switch, Link,Redirect, useLocation} from 'react-router-dom';
import SignIn from "./components/SignIn";
import ProfilePage from "./pages/ProfilePage";
import ZombieMath from "./pages/ZombieMath"
import PasswordReset from "./components/PasswordReset";
import Home from "./pages/Home";
import About from "./pages/About";
import SignUp from "./components/SignUp";
import News from "./pages/News";
import UserProvider,{UserContext} from './providers/UserProvider.jsx';
import logo from './img/logo.png';
import {auth} from './firebase.js';
import './utils/navbar/navbar.css';
/*
import ProfilePage from ".components/ProfilePage";
import PasswordReset from "./components/PasswordReset";
*/
function Application(){

  let user = useContext(UserContext);
  const[showSidebar,toggleShowSidebar] = React.useState(true);

  let location = useLocation();
  useEffect(()=>
  {
    location.pathname === "./ProfilePage" ? toggleShowSidebar(false) : toggleShowSidebar(true);
    console.log(location.pathname);

  },[location])

  const [theLocation,changeLocation] = React.useState(location);
  /*
  function inUpOut(){
    console.log(location);
    if(user){
      return <button onClick={() => auth.signOut() }> Sign Out </button>
    } else if (location.pathname === '/SignIn'){
      return  <Link to="./SignUp"> Sign Up </Link>
    } else {
      return  <Link to="./SignIn"> Sign In </Link>
    }
  }
  */
  let inUpOut;
  useEffect(() =>{

      if(user){
        inUpOut = <button onClick={() => auth.signOut() }> Sign Out </button>
      } else if (location.pathname === '/SignIn'){
        inUpOut = <Link to="./SignUp"> Sign Up </Link>

      } else {
        inUpOut = <Link to="./SignIn"> Sign In </Link>
    }

  } )





    return(
      <BrowserRouter>

      <div className="border navbar mx-auto bg-black text-white w-full inline-block shadow-md  font-Actor text-Actor">
        <div className="signinBox mr-5 float-left overflow-hidden">
           {!user?
          <Link to="/SignIn">
          <img src={logo} className="userPhoto hover:text-white-300" />
          </Link>
          :
          <Link to="/ProfilePage">
          <div
              style={{
                background:
                `url('${user.photoURL}') no-repeat center center`,
                backgroundSize: "cover",
                height: "100%",
                width: "100%"
              }}
              className="border border-blue-300 w-3/4 h-3/4 m-auto "
            />
          </Link>
          }
        </div>
        <ul className="my-5 bg-opacity-.5">
          <li className="mx-5 w-15 float-left hover:bg-gray-700  hover:text-blue-100">
            <Link to="/Home">Home</Link>
          </li>
          <li className="mx-5 w-15 float-left hover:bg-gray-700 hover:text-blue-100">
            <Link to="/About">About</Link>
          </li>
          <li className="mx-5 w-15 hover:bg-gray-700 float-left hover:text-blue-100">
            <Link to="/News">News</Link>
          </li>
          <li className="mx-5 w-15 hover:bg-gray-700  float-left hover:text-blue-100">
            <Link to="/ZombieMath">Zombie Math</Link>
          </li>
          </ul>
          <div className="inOut float-right mx-5  hover:bg-gray-500 hover:text-blue-100" >
            {user?  <button onClick={() => auth.signOut() }> Sign Out </button> : <Link to="./SignIn"> Sign In </Link> }

          </div>


      </div>
      <div className={`rest_of_form`} >
      {showSidebar  ? <div className="sidebar  w-2/12 absolute border-blue-300   border container text-center  inline-block ">
           <div className="sidebar_photo_container items-start w-half text-center mt-10">
             {user?   <div
                 style={{
                   background:
                   `url('${user.photoURL}') no-repeat center center`,
                   backgroundSize: "cover",
                   height: "100px",
                   width: "100px"
                 }}
                 className="border border-blue-300 w-3/4 h-3/4 m-auto "
               /> :   <img src={logo} className="userPhoto  w-3/4 h-3/4 m-auto  hover:text-white-300 opacity-25" />  }
             {user? <h5> Welcome, {user.displayName}  </h5> : <h5> Welcome, Please <Link to="./SignIn"><span className="hover:text-blue-200">Sign In </span></Link> </h5> }
           </div>
           <ul>
            <li onClick={()=>{console.log(user.photoURL)}}>Edit Username </li>
            <li>Edit Profile Picture </li>
            <li>Edit Username </li>
            <li>Edit Profile Picture </li>
          </ul>
        </div> : <div> </div>}



      <Switch>

        <Route path="/ProfilePage">
          {user?

          <ProfilePage sidebar={false} />
          :
          <Redirect to="/SignIn" />
          }
        </Route>

        <Route path="/Home" exact>
          <Home />
        </Route>
        <Route path="/News" exact>
          <News />
        </Route>
        <Route path="/About">
          <About />
        </Route>
        <Route path="/PasswordReset" >
          <PasswordReset />
        </Route>
        <Route path="/SignIn" >
          {user?
          <Redirect to="/ProfilePage" />
          :
          <SignIn />
          }
        </Route>
        <Route path="/SignUp" exact>
        <SignUp />
        </Route>
        <Route path="/Home" exact>
          <Home />
        </Route>
        <Route path="/ZombieMath" exact>
          <ZombieMath />
        </Route>




      </Switch>
      </div>

      </BrowserRouter>


    )
}


export default Application;
