import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import myFirebase from './firebase.js';
import Signup from './utils/signup/SignupForm';
import Signin from './utils/signup/Signin';

import './App.css';
import Navbar from './utils/navbar/Navbar';


class App extends Component {

  constructor(){
    super();
    this.state = {
      allUsers: [ ],
      signedIn: false,
      currentUser: "default"
    }
    this.firebaseDB = myFirebase.getFirebaseRef('Users');
    this.firebaseDB.once('value').then((snapshot) => {
      console.log(snapshot.val());
      this.addUser(snapshot.val());
    } )

  }


  addUser(users){
    let newUsers = Object.assign([], this.state.allUsers);

    if(users){
      for(let key in users){
        newUsers.push({
          name: users[key].name,
          username: users[key].username,
          password: users[key].age
        });
      }
    }

    this.setState({
      allUsers: newUsers,
    })
    console.log(this.state.allUsers.length);

  }
  handleDBChange(event){

  }
  changeIt = () =>
     <ul id="userTree">
      {this.state.allUsers.map((user) => <li>{user.username} {user.name} </li> )}

    </ul>

  render(){
    let signUpForm = this.state.signedIn ? <div> {this.state.user} </div> :  <Signup myFirebaseDB={this.firebaseDB} userBase={this.state.allUsers} />;
    let signInForm = <Signin />;
    return(
      <div>
      <Navbar user={"none"} />
      <h1> Hello! </h1>

      </div>
    )
  }








}

export default App;
