import React, {useContext, Component, UseState} from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter, Route, Switch, Link,Redirect} from 'react-router-dom';
import SignIn from "./components/SignIn";
import ProfilePage from "./pages/ProfilePage";
import PasswordReset from "./components/PasswordReset";
import Home from "./pages/Home";
import About from "./pages/About";
import SignUp from "./components/SignUp";
import UserProvider,{UserContext} from './providers/UserProvider.jsx';
import logo from './img/logo.png';
import {auth} from './firebase.js';
import './utils/navbar/navbar.css';
/*
import ProfilePage from ".components/ProfilePage";
import PasswordReset from "./components/PasswordReset";
*/
function Application(){

  let user = useContext(UserContext);

    return(



      <BrowserRouter>
      <div className="navbar mx-auto flex bg-gray-600 w-full shadow">
        <div className="block signinBox mr-5">
           {!user?
          <Link to="/SignIn">
          <img src={logo} className="userPhoto" />
          </Link>
          :
          <Link to="/ProfilePage">
          <img src={logo} className="userPhoto" />
          </Link>
          }
        </div>
        <ul className="flex my-5 float-left">
          <li className="mx-5">
            <Link to="/Home">Home</Link>
          </li>
          <li className="mx-5 ">
            <Link to="/About">About</Link>
          </li>
          <li className="mx-5 ">
            <Link to="/News">News</Link>
          </li>
          </ul>
          <div className="flex flex-row-reverse inOut ml-15 float-right my-5" >
           {user ? <button onClick={() => auth.signOut() }> Sign Out </button> :
           <button> <Link to="./signin"> Sign In </Link> </button> }

          </div>

      </div>
      <Switch>

        <Route path="/ProfilePage">
          {user?
          <ProfilePage />
          :
          <Redirect to="/SignIn" />
          }
        </Route>
        <Route path="/Home" exact>
          <Home />
        </Route>
        <Route path="/About">
          <About />
        </Route>
        <Route path="/PasswordReset" >
          <PasswordReset />
        </Route>
        <Route path="/SignIn" >
          {user?
          <Redirect to="/ProfilePage" />
          :
          <SignIn />
          }
        </Route>
        <Route path="/SignUp" exact>
        <SignUp />
        <Route path="/">
          <Home />
        </Route>

        </Route>




      </Switch>

      </BrowserRouter>


    )
}


export default Application;
