import React, {useContext} from "react";
import {UserContext} from "../providers/UserProvider";

import {auth,generateUserDocument,firestore,getUserDocument} from 'C:\\Users\\Noah Wotring\\Desktop\\Code\\ReactUserDataPractice\\my-app\\src\\firebase.js';
const ProfilePage = () => {
  const user = useContext(UserContext) || {
                                           displayName: "Noah Wotring",
                                           password: "L0w_L3v31",
                                           email: "noah13wotring@outlook.com",
                                           photoURL: 'https://lh3.googleusercontent.com/proxy/vzOX1KBH9QqNijnYZbx9I7-D_VbtaKkL3oYUupVZbt0jOFYsOY1YYpI9OqoN9JyrxFghCsfuHmrmmj6n-Ai3BokbfzgI8wBwtt3oSZeQqE6_ohlVqmyitL1LYxueO2_ve-sPvqjp6FmpfJw'

                                              };

  const nonUser = {

  };
  let theuser;
  const otherUser = async uid => {
    const getUser = await firestore.doc(`users/${uid}`).get();
     theuser = getUser.data();
     return theuser
  }


  const {photoURL, displayName, email, password} = user;


  return(
    <div className="mx-auto w-11/12 md:w-2/4 py-8 px-4 md:px-8">
      <h2>Welcome,{user.uid} or {user.password} </h2>
      <div className="flex border flex-col items-left md:flex-row md:items-start border-blue-400 px-3 py-4">
        <div
          style={{
            background:
            `url(${'https://lh3.googleusercontent.com/proxy/vzOX1KBH9QqNijnYZbx9I7-D_VbtaKkL3oYUupVZbt0jOFYsOY1YYpI9OqoN9JyrxFghCsfuHmrmmj6n-Ai3BokbfzgI8wBwtt3oSZeQqE6_ohlVqmyitL1LYxueO2_ve-sPvqjp6FmpfJw' || photoURL}) no-repeat center center`,
            backgroundSize: "cover",
            height: "200px",
            width: "200px"
          }}
          className="border border-blue-300"
        />
        <div className= "md:pl-4">
          <h2 className="text-2x1 font-semibold">{displayName}</h2>
          <h3 className= "italic">{email}</h3>
        </div>
      </div>
      <button onClick={() => console.log(otherUser())}> Show ID </button>
      <button className="w-full py-3 bg-red-600 mt-4 text-white" onClick={() => {auth.signOut()}}>Sign Out </button>
    </div>
  )
}

export default ProfilePage;
