import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import myFirebase from './firebase.js';
import Signup from './utils/signup/SignupForm';
import Signin from './utils/signup/Signin';
import Sidebar from './utils/navbar/Sidebar';
import { Router, Route, Link, browserHistory, IndexRoute } from 'react-router'

import Home from './page/Home';
import About from './page/About';
import Contact from './page/Contact';

import './App.css';
import Navbar from './utils/navbar/Navbar';


class App extends Component {

  constructor(){
    super();
    this.state = {
      allUsers: [ ],
      signedIn: false,
      user: null
    }
    this.firebaseDB = myFirebase.getFirebaseRef('Users');
    this.firebaseDB.once('value').then((snapshot) => {
      console.log(snapshot.val());
      this.addUser(snapshot.val());
    } )

  }


  addUser(users){
    let newUsers = Object.assign([], this.state.allUsers);

    if(users){
      for(let key in users){
        newUsers.push({
          name: users[key].name,
          username: users[key].username,
          password: users[key].age
        });
      }
    }

    this.setState({
      allUsers: newUsers,
    })
    console.log(this.state.allUsers.length);

  }
  handleDBChange(event){

  }
  changeIt = () =>
     <ul id="userTree">
      {this.state.allUsers.map((user) => <li>{user.username} {user.name} </li> )}

    </ul>

  render(){
    let signUpForm = this.state.signedIn ? <div> {this.state.user} </div> :  <Signup myFirebaseDB={this.firebaseDB} userBase={this.state.allUsers} />;
    let header = this.state.signedIn ? <div> <h1> Thank you for signing in, {this.state.user} </h1>  </div> :  <Signin />
    let navbar = <Navbar user={this.state.user} myFirebaseDB={this.firebaseDB} signedIn={this.state.signedIn} userBase={this.state.allUsers}  />;
    return(
      <div className="App">
      {navbar}
      <Sidebar />
      <div>
        <ul>
        <li>Home</li>
        <li> About </li>
        <li> Contact </li>
        </ul>
        {this.props.children}
      <switch>
        <Route path="/" component={Home} />
      </switch>
      </div>


      </div>
    )
  }








}

export default App;
