import React, {useEffect,useState} from 'react';
import simonsings from "../img/simonsings.png";
import zombiemath from "../img/zombiemath.png";
import experience from "../img/experience.png";
import color from "../img/colorplay.png";
import gossip from "../img/gossip.png";
import {Link} from "react-router-dom";
import "./css/home.css";


const Home = () => {

  const examples = [
    {
      name: "Come Visit",
      pic: experience,
      link: "./ComeVisit"
    },
    {
      name: "Gossip",
      pic: gossip,
      link: "./News"
    },
    {
      name: "ColorPlay",
      pic: color,
      link: "./ColorPlay"
    },
    {
      name: "Simon Sings",
      pic: simonsings,
      link: "./SimonSings"
    },
    {
      name: "ZombieMath",
      pic: zombiemath,
      link: "./ZombieMath"
    }
  ];
  const maxI = examples.length-1;

  const [currentApp,setApp] = useState(0);
  const [showing,rotate] = useState({
    previous: maxI,
    current: 0,
    next: 1
  })

  const [nextApp, setNextApp] = useState(currentApp + 1);
  const [previousApp, setPreviousApp] = useState(maxI)
  useEffect(() => {
    console.log(previousApp,currentApp, nextApp)
  },[currentApp])
  const rotateClockwiseHandler = () => {
    rotate({
      previous: rotateClockwiseHandler(showing.previous),
      current: rotateClockwiseHandler(showing.current),
      next: rotateClockwiseHandler(showing.next)

    })
    /*
    if(previousApp === maxI){
      setPreviousApp(0);
      setApp(1);
      setNextApp(2)

    } else if (currentApp === maxI){
      setApp(0);
      setPreviousApp(1);
      setNextApp(2)
    }
    else if (nextApp === maxI){
      setNextApp(0);
      setApp(currentApp+1);
      setPreviousApp(previousApp+1)
  }   else {
    console.log(previousApp,currentApp, nextApp)

      setPreviousApp(previousApp+1)
      setApp(currentApp+1)
      setNextApp(nextApp+1)
    }
    */
  }
  const rotateCounterHandler = () => {
    rotate({
      previous: rotateCounter(showing.previous),
      current: rotateCounter(showing.current),
      next: rotateCounter(showing.next)

    })

  }
  const rotateClockwise = (num) => {   return num === maxI ? 0 : num+1 }
  const rotateCounter = (num) => { return num === 0 ? maxI : num-1 }




  return(
   <div className="ml-16 relative container text-center  " >
    <h1 className="text-center text-5xl mt-10 font-semibold  homeH1 "> Not Another Portfolio! </h1>
    <p className="ml-48 block"> A Portfolio By Noah Kumar Wotring </p>

      <div className="portfolio_bar block">
        <div className="side  left mx-auto inline-block">
        <h3> {examples[showing.previous].name} </h3>

        <div
          style={{
            background:
            `url('${examples[showing.previous].pic}') no-repeat center center / cover`,
            height: "100%",
            width: "100%"
          }}
          className="hover:opacity-50 "
          onClick={rotateClockwiseHandler}

          />
        </div>

        <div className='center mx-auto inline-block'>
        <h1> {examples[showing.current].name} </h1>
        <Link to={examples[showing.current].link} >
        <div
          style={{
            background:
            `url('${examples[showing.current].pic}') no-repeat center center / cover`,
            height: "100%",
            width: "100%"
          }}
          className="hover:opacity-50 "
          />
          </Link>
        </div>

        <div className="side right mx-auto inline-block">
        <h3> {examples[showing.next].name} </h3>

        <div
          style={{
            background:
            `url('${examples[showing.next].pic}') no-repeat center center / cover`,
            height: "100%",
            width: "100%"
          }}
          className="hover:opacity-50 "
          />
        </div>
      </div>
     {/*
        <div className="porfolio_links mt-16 ml-8 contents inline-block w-1/4">
          <div className="container block float-left inline-block ">
            <h3 className=""> Come Visit </h3>
            <Link to="./ComeVisit">
            <div
              style={{
                background:
                `url('${experience}') no-repeat center center / cover`,
                height: "200px",
                width: "300px"
              }}
              className="hover:opacity-50 portolio_link"
              />
            </Link>
          </div>
        </div>
          <div className="porfolio_links mt-16 ml-8 contents inline-block w-1/4">
            <div className="container block float-left inline-block ">
              <h3 className=""> Color Play</h3>
              <Link to="./ColorPlay">
              <div
                style={{
                  background:
                  `url('${color}') no-repeat center center / cover`,
                  height: "200px",
                  width: "300px"
                }}
                className="hover:opacity-50 portolio_link"
                />
              </Link>
            </div>
          </div>
          <div className="porfolio_links mt-16 ml-8 contents inline-block w-1/4">
            <div className="container block float-left inline-block ">
              <h3 className=""> Gossip</h3>
              <Link to="./News">
              <div
                style={{
                  background:
                  `url('${gossip}') no-repeat center center / cover`,
                  height: "200px",
                  width: "300px"
                }}
                className="hover:opacity-50 portolio_link"
                />
              </Link>
            </div>
          </div>
          <div className="porfolio_links mt-16 ml-8 inline-block contents w-1/4">
            <div className="container block float-left inline-block ">
              <h3 className="">Zombie Math </h3>
              <Link to="./ZombieMath">
              <div
                style={{
                  background:
                  `url('${zombiemath}') no-repeat center center / cover`,
                  height: "200px",
                  width: "300px"
                }}
                className="hover:opacity-50 portolio_link"
                />
              </Link>
            </div>
          </div>
          <div className="porfolio_links mt-16 ml-8 contents inline-block w-1/4">
            <div className="container block float-left inline-block ">
              <h3 className=""> Simon Sings </h3>
              <Link to="./SimonSings">
                <div
                  style={{
                    background:
                      `url('${simonsings}') no-repeat center center / cover`,
                    height: "200px",
                    width: "300px"
                    }}
                  className="hover:opacity-50 portolio_link"
                />
              </Link>
            </div>
          </div>
          */}
    </div>
)


}
export default Home;
