import React, {Component, useContext,useState, useEffect} from 'react';
import SignIn from "../components/SignIn";
import NewsPost from '../components/NewsPost.js';
import './home.css';
import {generateNewsPosts,firestore}from  '../firebase.js'
import {UserContext} from "../providers/UserProvider";
import {TheNumContext} from '../providers/NumberProvider';

const Home = () => {
  const user = useContext(UserContext);
  const num = useContext(TheNumContext);
  const[posts,checkPosts] = useState([]);

  const mount = useEffect(() => {

  });

  const generateNewsPosts = async () => {
    let collectedPosts = [];
    const postsRef = firestore.collection('posts');
    const snapshot = await postsRef.get();
    try {
      const allPosts = await firestore.collection(`posts`).get();
      allPosts.forEach(doc => {
        collectedPosts.push({title: doc.data().title,
                              body: doc.data().body
                            })
      });
      collectedPosts.forEach((doc) => console.log(doc));
      checkPosts(collectedPosts);
      } catch (error) {
        console.error("Error fetching data", error)
      }
    }




  useEffect(() => {
    generateNewsPosts();
  })

    return (


      <div>
        <h1 className="text-6x1"> Home Yay</h1>
        <button onClick={() => console.log(posts)}> click </button>
        <div className="container news_post_container w-4/5 bg-blue-800 opacity-50 box item-center ">
          {posts.map((post) => <NewsPost body={post.body} title={post.title} />)}
         </div>

      </div>
    )
  }


export default Home;
