import React, {useState} from 'react';
import {BrowserRouter, Route, Switch, Link} from 'react-router-dom';
const SignUp = () => {
  const [email, setEmail] = useState("");
  const [password,setPassword ] = useState("");
  const [displayName, setDisplayName] = useState("");
  const [error,setError] = useState(null);
  const createUserWithEmailAndPassword = (event, email, password) => {
    event.preventDefault();
    setEmail("");
    setPassword("");
    setDisplayName("");
  };
  const onChangeHandler = (event) => {
    const {name, value} = event.currentTarget;
    if(name === "userEmail"){
      setEmail(value);
    } else if(name === "userPassword") {
      setPassword(value);
    } else if(name === "displayName") {
      setDisplayName(value);
    }
  };
  return(
    <div className="block mt-8">
    <h1 className="text-3x1 mb-2 text-center font-bold"> </h1>
    <div className="border border-blue-400 mx-auto w-11/12 md:w-2/4 rounded py-8 px-4 md:px-8">
    <form className="">
    <label className="block" htmlFor="displayName">
    Username: {" "}
    </label>
    <input
      type="displayName"
      name="displayName"
      value={displayName}
      className="my-1 p-1 w-full"
      id="displayName"
      placeholder="E.g: daCoolestDudeInDaWest"
      onChange = {(event) => onChangeHandler(event)}
    />
    <br />
    <label htmlFor="userEmail">
      E-Mail:{" "}
    </label>
    <input
      name="userEmail"
      type="email"
      id="userEmail"
      className="my-1 p-1 w-full"
      value={email}
      placeholder="E.g: noah123@gmail.com"
      onChange = {(event) => onChangeHandler(event)}
    />
    <br />
    <label className="block" htmlFor="userPassword">
      Password:{" "}
    </label>
    <input
      className="mt-1 mb-3 p-1 w-full"
      name="userPassword"
      type="password"
      id="userPassword"
      value={password}
      onChange={(event) => onChangeHandler(event)}
      placeholder="E.g: derpdede123"
    />
    <button className="bg-green-400 hover:bg-green-500 w-full py-2 text-white" onClick = {(event) => {createUserWithEmailAndPassword(event, email, password)}} >
      Sign Up
    </button>

    </form>
    </div>
    </div>





  )







}
export default SignUp;
